export const getDepartamentoPorCodigoAPI = async (id) => {
    const resposta = await fetch(`${process.env.REACT_APP_ENDERECO_API}/departamentos/${id}`);
    if (!resposta.ok) {
        throw new Error("Erro ao obter o departamento");
    }
    return await resposta.json();
};

export const deleteDepartamentoPorCodigoAPI = async id => {
    const response = await 
        fetch(`${process.env.REACT_APP_ENDERECO_API}/departamentos/${id}`,
            {
                method : "DELETE",
                headers : {
                    "Content-Type" : "application/json"
                }
            }
        );
    const data = await response.json();
    return data;
}

export const cadastraDepartamentoAPI = async (departamento, metodo) => {
    const url = metodo === "POST"
      ? 'http://127.0.0.1:3002/departamentos'
      : `http://127.0.0.1:3002/departamentos/${departamento.id}`;

    const response = await fetch(url, {
      method: metodo,
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(departamento)
    });

    if (!response.ok) {
      console.error(`Erro ao cadastrar: ${response.status} - ${response.statusText}`);
      return { status: "error", message: "Erro ao cadastrar departamento" };
    }

    try {
      const data = await response.json();
      return { status: "success", message: "Departamento cadastrado com sucesso", data };
    } catch (error) {
      console.error("Erro ao fazer parse do JSON:", error);
      return { status: "error", message: "Erro ao processar resposta da API" };
    }
  };
