import { useState, useEffect } from "react";
import {
    getDepartamentoAPI, getDepartamentoPorCodigoAPI, deleteDepartamentoPorCodigoAPI,
    cadastraDepartamentoAPI
} from "../../../servicos/DepartamentoServico";
import Tabela from "./Tabela";
import Formulario from "./Formulario";
import Carregando from "../../comuns/Carregando";
import '../Home.css'
import DepartamentoContext from "./DepartamentoContext";

function Departamento() {
    const [alerta, setAlerta] = useState({ status: "", message: "" });
    const [listaDepartamentos, setListaDepartamentos] = useState([]);
    const [editar, setEditar] = useState(false);
    const [exibirForm, setExibirForm] = useState(false);
    const [departamento, setDepartamento] = useState({
        id: 0,
        nome: "",
        localizacao: ""
    });
    const [carregando, setCarregando] = useState(false);

    const novoDepartamento = () => {
        setEditar(false);  // Garante que o formulário está no estado para criação
        setAlerta({ status: "", message: "" });
        setDepartamento({
            id: 0,
            nome: "",
            localizacao: ""
        });
        setExibirForm(true);  // Exibe o formulário de criação
    }

    const editarDepartamento = async (id) => {
        try {
            const dados = await getDepartamentoPorCodigoAPI(id); // Obtém os dados do departamento
            setDepartamento(dados); // Preenche o estado do departamento com os dados obtidos
            setEditar(true); // Define o modo de edição
            setExibirForm(true); // Exibe o formulário
        } catch (erro) {
            console.error("Erro ao buscar o departamento para edição:", erro);
            setAlerta({ status: "error", message: "Erro ao carregar o departamento" });
        }
    };

    const acaoCadastrar = async e => {
        e.preventDefault();
        const metodo = editar ? "PUT" : "POST";
        try {
            let retornoAPI = await cadastraDepartamentoAPI(departamento, metodo);
            setAlerta({ status: retornoAPI.status, message: retornoAPI.message });
            setDepartamento(retornoAPI.departamento);
            if (!editar) {
                setEditar(true);
            }
        } catch (err) {
            console.log("Erro: " + err);
        }
        recuperaDepartamentos();
    };

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        setDepartamento({ ...departamento, [name]: value });
    };

    const recuperaDepartamentos = async () => {
        setCarregando(true);
        const departamentos = await getDepartamentoAPI();
        setListaDepartamentos(departamentos);
        console.log(departamentos); // Verifique se os dados estão sendo carregados corretamente
        setCarregando(false);
    }

    const remover = async id => {
        if (window.confirm('Deseja remover este departamento?')) {
            let retornoAPI = await deleteDepartamentoPorCodigoAPI(id);
            setAlerta({
                status: retornoAPI.status,
                message: retornoAPI.message
            });
            recuperaDepartamentos();
        }
    };

    useEffect(() => {
        recuperaDepartamentos();  // Recupera os departamentos ao montar o componente
    }, []);


    return (
        <div className="departamento">
            {/* O DepartamentoContext.Provider é onde você passa os valores para os componentes filhos */}
            <DepartamentoContext.Provider value={{
                alerta, 
                listaDepartamentos, 
                remover, 
                departamento, 
                editarDepartamento,
                novoDepartamento, 
                acaoCadastrar, 
                handleChange, 
                exibirForm, 
                setExibirForm
            }}>
                <div className="tabela-container">
                    <Carregando carregando={carregando}>
                        <Tabela />
                    </Carregando>
                </div>
                <Formulario />
            </DepartamentoContext.Provider>
        </div>
    );
}

export default Departamento;
