import { useContext } from "react";
import Alerta from "../../comuns/Alerta";
import { Table, Button } from "react-bootstrap";
import DepartamentoContext from "./DepartamentoContext"; // Contexto alterado para DepartamentoContext

function Tabela() {

    // Pegando os dados do contexto
    const { alerta, listaDepartamentos, remover, novoDepartamento, editarDepartamento } = useContext(DepartamentoContext);

    // Verificando se a listaDepartamentos está recebendo os dados corretamente
    console.log(listaDepartamentos);

    return (
        <div style={{ padding: '20px' }} className="tabela">
            <h1 style={{ color: 'white', textAlign: "center" }}>Painel de Controle: Departamentos</h1>

            {/* Alerta para mensagens de erro/sucesso */}
            <Alerta alerta={alerta} />

            {/* Botão para adicionar novo departamento */}
            <Button variant="primary" onClick={() => novoDepartamento()}>
                Adicionar Novo Departamento
            </Button>

            <br /><br />

            {/* Caso não haja nenhum departamento */}
            {listaDepartamentos.length === 0 && (
                <h1>Nenhum registro encontrado</h1>
            )}

            {/* Caso haja departamentos para exibir */}
            {listaDepartamentos.length > 0 && (
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th style={{ textAlign: 'center' }}>Ações</th>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Localização</th>
                        </tr>
                    </thead>
                    <tbody>
                        {listaDepartamentos.map((departamento) => (
                            <tr key={departamento.id}>
                                <td align="center">
                                    <Button variant="info" onClick={() => editarDepartamento(departamento.id)}>
                                        <i className="bi bi-pencil-square"></i>
                                    </Button>
                                    <Button variant="danger" onClick={() => remover(departamento.id)}>
                                        <i className="bi bi-trash"></i>
                                    </Button>
                                </td>
                                <td>{departamento.id}</td> {/* Exibe o ID do Departamento */}
                                <td>{departamento.nome}</td> {/* Exibe o Nome do Departamento */}
                                <td>{departamento.localizacao}</td> {/* Exibe a Localização do Departamento */}
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
        </div>
    );
}

export default Tabela;
