import { useContext, useState, useEffect } from "react";
import Alerta from "../../comuns/Alerta";
import { Table } from "react-bootstrap";
import { Button } from "react-bootstrap";
import DepartamentoContext from "./DepartamentoContext"; // Contexto alterado para DepartamentoContext

function Tabela() {
    const { alerta, remover, editarDepartamento } = useContext(DepartamentoContext); 
    const [listaDepartamentos, setListaDepartamentos] = useState([]);  // Lista de departamentos que virá da API

    // Buscar departamentos na API
    useEffect(() => {
        const apiUrl = process.env.REACT_APP_ENDERECO_API + '/departamentos';  // Montando a URL da API com a variável de ambiente

        // Realizando a requisição GET para a API
        fetch(apiUrl)
            .then(response => response.json())  // Convertendo a resposta para JSON
            .then(data => {
                setListaDepartamentos(data);  // Atualizando o estado com os dados dos departamentos
            })
            .catch(error => {
                console.error('Erro ao carregar os departamentos:', error);
            });
    }, []);  // O array vazio [] significa que o useEffect rodará apenas uma vez, quando o componente for montado

    return (
        <div style={{ padding: '20px' }} className="tabela">
            <h1 style={{ color: 'white', textAlign: "center" }}>Painel de Controle: Departamentos</h1>
            <Alerta alerta={alerta} />
            <Button variant="primary" onClick={() => novoDepartamento()}>
                Adicionar Novo Departamento
            </Button>
            <br /><br />
            {listaDepartamentos.length === 0 &&
                <h1>Nenhum registro encontrado</h1>}
            {listaDepartamentos.length > 0 && (
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th style={{ textAlign: 'center' }}>Ações</th>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Localização</th>
                        </tr>
                    </thead>
                    <tbody>
                        {listaDepartamentos.map((departamento) => (
                            <tr key={departamento.id}>
                                <td align="center">
                                    <Button variant="info"
                                        onClick={() => editarDepartamento(departamento.id)}>
                                        <i className="bi bi-pencil-square"></i>
                                    </Button>
                                    <Button variant="danger"
                                        onClick={() => remover(departamento.id)}>
                                        <i className="bi bi-trash"></i>
                                    </Button>
                                </td>
                                <td>{departamento.id}</td>
                                <td>{departamento.nome}</td>
                                <td>{departamento.localizacao}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
        </div>
    );
}

export default Tabela;
